import { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
// import FormControl from '@material-ui/core/FormControl';
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";

const useStyles = makeStyles({
  paper: {
    margin: "auto",
    width: 650,
  },
});

function RecipePage(props) {
  const classes = useStyles();

  //ComponentDidMount() { }
  useEffect(() => {
    if (props.recipes.length === 0) {
      props.loadRecipes();
    }
  }, []);

  return (
    <TableContainer className={classes.paper} component={Paper}>
      <Table>

        <TableHead>
          <TableRow>
            <TableCell>Id</TableCell>
            <TableCell>Title</TableCell>
            <TableCell>Category</TableCell>
          </TableRow>
        </TableHead>

        <TableBody>
          {props.recipes.map((recipe) => (
            <TableRow key={recipe.id}>
              <TableCell>{recipe.id}</TableCell>
              <TableCell>{recipe.recipeName}</TableCell>
              <TableCell>{recipe.measurement}</TableCell>
              <TableCell align="right">{recipe.ingredient}</TableCell>
              <TableCell align="right">{recipe.addtlInfo}</TableCell>
              <TableCell>{recipe.instructionsBox}</TableCell>
            </TableRow>
          ))}
        </TableBody>


      </Table>
    </TableContainer>
  );
}

export default RecipePage;
