import * as ActionTypes from "../actions/actionTypes.js";

export default function recipeReducer(state = [], action) {
  switch (action.type) {
    case ActionTypes.LOAD_RECIPES_SUCCESS:
      return action.recipes;
    case ActionTypes.POST_RECIPES:
      return [...state, action.recipes];
    case ActionTypes.DELETE_BOOK_SUCCESS:
      return state.filter( b => b.id) //the filter will not modify the existing array but it will return the new array
    default:
      return state;
  }
}
