import * as ActionTypes from "./actionTypes";
import * as recipeApi from "../../api/recipeApi";

export function loadRecipes() {
  return function (dispatch) {
    return recipeApi
      .getRecipes()
      .then((booksFromApi) => dispatch(loadRecipesSuccess(recipesFromApi)))
      .catch((error) => console.log(error));
  };
}

export function loadRecipesSuccess(recipes) {
  return {
    type: ActionTypes.LOAD_RECIPES_SUCCESS,
    recipes,
  };
}

export function addRecipe(recipe) {
  return function (dispatch) {
    return recipeApi
      .add(recipe)
      .then((newRecipe) => dispatch(addRecipeSuccess(newRecipe)))
      .catch((error) => console.log(error));
  };
}

export function addRecipeSuccess(recipe) {
  return {
    type: ActionTypes.ADD_RECIPES,
    recipe,
  };
}
